select s.id, s.user_id, s.sid, s.mobile, s.province_code, s.cmcp, s.title, s.content, s.attach, s.passage_id, s.need_push, s.push_url,s.create_time, s.create_unixtime, s.status, s.remark, s.msg_id,s.destnation_no, sd.mobile deliver_mobile,sd.id deliver_id,sd.status_code deliver_status_code, sd.status deliver_status,sd.deliver_time,sd.create_time deliver_create_time,sd.remark deliver_remark from mms_mt_message_submit s left join mms_mt_message_deliver sd on sd.msg_id = s.msg_id and sd.mobile = s.mobile where 1 = 1
and s.user_id = #{userId}
and s.sid = #{sid}
and s.mobile like '${mobile}%'
and s.title like '%${title}%'
and s.passage_id = #{passageId}
and s.create_unixtime >=#{startDate}
and s.create_unixtime <=#{endDate}
and s.status = #{sendStatus}
and sd.status is null
and sd.status = #{deliverStatus}
order by s.create_unixtime desc limit #{start},#{end}

